//据绝对一致性:当进程获得进程锁时,将会强制重新获取SESSION,一般需要在SESSION中存放缓存数据时才使用

exports = module.exports = {
    key     : "sid",                             //session id key
    guid    : true,                              //使用guid作为session id
    method  : "cookie",                          //session id 存储方式,get,post,path,cookie

    level  : 1,                                //安全等级，0:不验证,1:基本验证,2:基本验证+进程锁,3:基本验证+进程锁+数据绝对一致性
    redis  : null,                             //redis连接
    format : null,                             //session 数据序列化
    secret : Date.now().toString(),            //加密字符串
    prefix : "session",                       //session hash 前缀
    expire : 86400,                             //有效期,S
    lockNum : 5,
    lockTime : 200,
}