"use strict";
const cosjs_library    = require('cosjs.library');
//spirit 存在于每个工作进程负责与master通信
exports = module.exports =  function socket_spirit(opts){
    var emit = opts.emit || 'redis';
    switch (emit){
        case 'redis':
            return spirit_redis(opts);
        default:
            return spirit_cluster(opts);
    }
};

function spirit_redis(opts){
    var emitter = cosjs_library('emitter');
    return emitter(opts);
}



